import React from 'react';

const Slide4 = ({ navigate }) => {
  console.log('Rendering Slide4');

  const handleEggClick = (number) => {
    console.log(`Egg ${number} clicked`);
    // Add your navigation or action here
  };

  return (
    <div style={styles.container}>
      <div style={styles.imageContainer}>
        <img src="/images/File4.svg" alt="Slide 4" style={styles.image} />
        <img 
          src="/images/egg1.svg" 
          alt="Egg 1" 
          style={{ ...styles.egg, left: '8%', bottom: '5%' }} 
          onClick={() => handleEggClick(1)} 
        />
        <img 
          src="/images/egg3.svg" 
          alt="Egg 3" 
          style={{ ...styles.egg, left: '38%', bottom: '5%' }} 
          onClick={() => handleEggClick(3)} 
        />
        <img 
          src="/images/egg5.svg" 
          alt="Egg 5" 
          style={{ ...styles.egg, left: '75%', bottom: '1.3%' }} 
          onClick={() => handleEggClick(5)} 
        />
      </div>
    </div>
  );
};

const styles = {
  container: {
    textAlign: 'center',
    height: '100vh',
    backgroundColor: '#9fd3c7',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    position: 'relative'
  },
  imageContainer: {
    position: 'relative',
    width: '100%', // Make the container full width
    maxWidth: '400px', // Set a maximum width to control scaling
    height: 'auto'
  },
  image: { 
    width: '100%', 
    height: 'auto' 
  },
  egg: { 
    position: 'absolute',
    width: '100px', // Adjust size as needed
    height: '100px',
    cursor: 'pointer'
  }
};

export default Slide4;
